import "./Login.css";
import React from "react";
import { useEffect, useState } from "react";
import Axios from "axios";

function Login() {
    const [uid,setUid] = useState(0);
    const [pass,setPass] = useState("");
    const [itemlist,setItemList] = useState([]);


    async  function login() {
        await Axios.post("http://localhost:3001/login",{
            uid: uid,
            pass: pass,
        }).then((response) => {           
            if (response.data.message) 
            {
                alert(response.data.message);
            }else {
                alert("")
            }
        }).catch((err)=>{
            console.log(err);
        });
    };

    return(
        <div className = "page">
            <div className="layer">
            <div className = "box">
                <div id="login" className="input-group">
                    <input type="text" className ="input-field" placeholder="User ID" onChange={(event) => {setUid(event.target.value);}}required/>
                    <input type="text" className ="input-field" placeholder="Password" onChange={(event) => {setPass(event.target.value);}}required/>
                    <button onClick={login} className="submit-btn">Log In</button>
                    <button type="submit" className="submit-btn">Sign Up</button>
                </div>
            </div>

        </div>
    </div>
    );
}
export default Login;
