import "./App.css";
import { useEffect, useState } from "react";
import Axios from "axios";

function App() {

    const [itemlist,setItemList] = useState([]);

    useEffect(()=> {
        Axios.get("http://localhost:3001/items").then((response) => {
            setItemList(response.data);
        }).catch((err)=>{
            console.log(err);
        })
    },[])

    const deleteItem = (id) => {
        Axios.delete(`http://localhost:3001/delete/${id}`).then((response) => {
          setItemList(
            itemlist.filter((val) => {
              return val.ItemID != id;
            })
          );
        });
      };

    return (
        <div className="App">
            <input type="text" placeholder="Search"/>

            <button type="button" onClick="document.getElementById('myModal').style.display='block'"id="mybtn">ADD ITEM</button>

            <div id="myModal" class="modal">
                <div class="modal-content">
                    <span onClick="document.getElementById('myModal').style.display='none'"class="close">&times;</span>

                    <form action="/url" method="GET">

                        <label for="ItemID">ItemID</label>      
                        <input type="text" id="ID" name="ID" placeholder="ID" required/>
                        <br/> <br/>

                        <label for="Item Name">Item Name</label>
                        <input type="text" id="name" name="ItemName" placeholder="NAME" required/>
                        <br/> <br/>

                        <label for="Availability">Availability</label>

                        <select name="Availability" id="Availability">
                            <option value="from home">From Home</option>
                            <option value="available in roorkee">Available In Roorkee</option>
                            <option value="available in campus">Available In Campus</option>
                        </select>

                    </form>
                    <div class="align-right">
                        <button type="submit" class="button" onclick="alert('value is '+document.getElementById('name').value);document.getElementById('myModal').style.display='none'" >ADD</button>
                    </div>
                </div>
            </div>
            <table>
                <tbody>
                    <tr>
                        <th>Item Id</th>
                        <th>Item Name</th>
                        <th>Availability</th>
                    </tr>
                </tbody>
                {itemlist.map((val,key)=> {
                    return (
                        <div className="item" key={key}>
                            <tr>
                                <td>{val.ItemID}</td>
                                <td>{val.Item_Name}</td>
                                <td>{val.Availability}</td>
                                <button onClick={()=> {deleteItem(val.ItemID)}}> Remove </button>
                            </tr>
                        </div>
                    )
                })}
            </table>
        </div>
    )
}

export default App;
