import React from "react";
import { useState } from "react"
import "./Modal.css";

function Modal({setOpenModal}) {

    const [id,setID] = useS
    return(
    <div id="myModal" className="modal">
    <div className="modal-content">

            <button
                onClick={() => {
                    setOpenModal(false);
                }}
            >
                X
            </button>

            <form action="/url" method="GET">

            <label>ItemID</label>      
            <input type="text" id="ID" name="ID" placeholder="ID" required/>
            <br/> <br/>

            <label>Item Name</label>
            <input type="text" id="name" name="ItemName" placeholder="NAME" required/>
            <br/> <br/>

            <label>Availability</label>

            <select name="Availability" id="Availability">
                <option value="from home">From Home</option>
                <option value="available in roorkee">Available In Roorkee</option>
                <option value="available in campus">Available In Campus</option>
            </select>

        </form>
        <div className="align-right">
            <button type="submit" className="button">ADD</button>
        </div>
    </div>
    </div>
    );
}

export default Modal;
